import React, { useState } from 'react';
import Box from '@mui/material/Box';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemAvatar from '@mui/material/ListItemAvatar';
import ListItemText from '@mui/material/ListItemText';
import IconButton from '@mui/material/IconButton';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import Grid from '@mui/material/Grid';

import CloseIcon from '@mui/icons-material/Close';
import RadioButtonUncheckedIcon from '@mui/icons-material/RadioButtonUnchecked';
import TaskAltIcon from '@mui/icons-material/TaskAlt';

function generate(element, index) {
  return React.cloneElement(element, {
      key: index,
  });
}

export default function InteractiveList() {
    const [readList, setReadList] = React.useState([false, false, false]);

  const readAlarm = (index) => {
    setReadList((preReadList) => {
      const newReadList = [...preReadList];
      newReadList[index] = !newReadList[index];
      return newReadList;
    });
  };

  const icon = read ? <TaskAltIcon/> : <RadioButtonUncheckedIcon />
  

  return (
    <Box>
      <h2>알림리스트</h2>
      <FormControlLabel
          control={
            <Checkbox
                checked={readList.every((item) => item)}
                onChange={() => setReadList((prevReadList) => prevReadList.map(() => !prevReadList[0]))}
            />
          }
          label="모두 읽음"
        />
      
      <Grid>
        
        <Grid>
          
          <div>
            <List read={read}>
              {generate(
                <ListItem
                  id='alarm-item'
                  secondaryAction={
                    <IconButton edge="end" onClick={() => readAlarm(index)}>
                      <CloseIcon />
                    </IconButton>
                  }
                >
                  <ListItemAvatar>
                    {icon}
                  </ListItemAvatar>
                  <ListItemText primary="갈망포카" secondary="지금 당신의 갈망포카가 올라왔어요! 확인해보세요." />
                  
                </ListItem>,
                
              )}
            </List>
            
          </div>
        </Grid>
      </Grid>
    </Box>
  );
}
