import React from "react";
import AlarmList from "./AlarmList";

const AlarmItem = () => {
  const alarmTypes = ["갈망포카", "채팅알람", "갈망포가"];
  const alarmTexts = [
    "지금 당신의 갈망포카가 올라왔어요! 확인해보세요.",
    "{} 님이 채팅을 보냈습니다.",
    "지금 당신의 갈망포카가 올라왔어요! 확인해보세요.",
  ];
  const alarmTimes = [
    "2024-01-23 19:24",
    "2024-01-23 19:34",
    "2024-01-23 19:54",
  ];

  return (
    <div>
      {/* <AlarmList readList={} setReadList={} alarmTexts={alarmTexts} /> */}
      <List>
        {readList.map((isRead, index) =>
          generate(
            <ListItem
              secondaryAction={
                <>
                  <span id="alarm-time">{alarmTimes}</span>
                  <IconButton edge="end" onClick={() => readAlarm(index)}>
                    {isRead ? null : <CloseIcon />}
                  </IconButton>
                </>
              }
            >
              <ListItemAvatar>
                {isRead ? <TaskAltIcon /> : <RadioButtonUncheckedIcon />}
              </ListItemAvatar>
              <ListItemText primary="{alarmTypes}" secondary="{alarmTexts}" />
            </ListItem>,
            index,
            isRead
          )
        )}
      </List>
    </div>
  );
};

export default AlarmItem;
