import React, { useState } from "react";
import Box from "@mui/material/Box";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import ListItemAvatar from "@mui/material/ListItemAvatar";
import ListItemText from "@mui/material/ListItemText";
import IconButton from "@mui/material/IconButton";
import FormControlLabel from "@mui/material/FormControlLabel";
import Checkbox from "@mui/material/Checkbox";
import Grid from "@mui/material/Grid";

import CloseIcon from "@mui/icons-material/Close";
import RadioButtonUncheckedIcon from "@mui/icons-material/RadioButtonUnchecked";
import TaskAltIcon from "@mui/icons-material/TaskAlt";
import { Container } from "@mui/material";

function generate(element, index, isRead) {
  return React.cloneElement(element, {
    key: index,
    className: isRead ? "alarm-read-item" : "alarm-item",
  });
}

export default function InteractiveList() {
  const [readList, setReadList] = React.useState([false, false, false]);

  const readAlarm = (index) => {
    setReadList((preReadList) => {
      const newReadList = [...preReadList];
      newReadList[index] = !newReadList[index];
      return newReadList;
    });
  };

  return (
    <Container>
      <div>
        <h2>알림리스트</h2>
        <FormControlLabel
          id="alarm-check-all"
          control={
            <Checkbox
              checked={readList.every((item) => item)}
              onChange={() =>
                setReadList((preReadList) =>
                  preReadList.map(() => !preReadList[0])
                )
              }
            />
          }
          label="모두 읽음"
        />
      </div>

      <div>
        <List>
          {readList.map((isRead, index) =>
            generate(
              <ListItem
                secondaryAction={
                  <IconButton edge="end" onClick={() => readAlarm(index)}>
                    <CloseIcon />
                  </IconButton>
                }
              >
                <ListItemAvatar>
                  {isRead ? <TaskAltIcon /> : <RadioButtonUncheckedIcon />}
                </ListItemAvatar>
                <ListItemText
                  primary="갈망포카"
                  secondary="지금 당신의 갈망포카가 올라왔어요! 확인해보세요."
                />
              </ListItem>,
              index,
              isRead
            )
          )}
        </List>
      </div>
    </Container>
  );
}
