import React, { useState } from "react";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import ListItemAvatar from "@mui/material/ListItemAvatar";
import ListItemText from "@mui/material/ListItemText";
import IconButton from "@mui/material/IconButton";
import FormControlLabel from "@mui/material/FormControlLabel";
import Checkbox from "@mui/material/Checkbox";

import CloseIcon from "@mui/icons-material/Close";
import RadioButtonUncheckedIcon from "@mui/icons-material/RadioButtonUnchecked";
import TaskAltIcon from "@mui/icons-material/TaskAlt";
import { Container } from "@mui/material";

import AlarmItem from "./AlarmItem";

const AlarmList = ({ readList, setReadList, alarmTexts }) => {
  const readAll = () => {
    setReadList((preReadList) => preReadList.map(() => true));
  };

  return (
    <div className="AlarmList">
      <h2>알림리스트</h2>
      <div>
        <FormControlLabel
          id="alarm-check-all"
          control={
            <Checkbox
              checked={readList.every((item) => item)}
              onChange={readAll}
              disabled={readList.every((item) => item)}
            />
          }
          label="모두 읽음"
        />
      </div>
      <div>
        {readList.map((isRead, index) => (
          <AlarmItem
            key={index}
            isRead={isRead}
            readAlarm={setReadList}
            index={index}
            type={alarmType[index]}
            text={alarmTexts[index]}
            time={alarmtime[index]}
          />
        ))}
      </div>
    </div>
  );
};

export default AlarmList;
