import React, { useState } from 'react';
import Box from '@mui/material/Box';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemAvatar from '@mui/material/ListItemAvatar';
import ListItemText from '@mui/material/ListItemText';
import IconButton from '@mui/material/IconButton';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import Grid from '@mui/material/Grid';

import CloseIcon from '@mui/icons-material/Close';
import RadioButtonUncheckedIcon from '@mui/icons-material/RadioButtonUnchecked';
import TaskAltIcon from '@mui/icons-material/TaskAlt';

function generate(element) {
  return [0, 1, 2].map((value) =>
    React.cloneElement(element, {
      key: value,
    }),
  );
}

export default function InteractiveList() {
  const [read, setRead] = React.useState(false);

  const readAlarm = () => {
    setRead((readState) => !readState);
  }

  const icon = read ? <TaskAltIcon/> : <RadioButtonUncheckedIcon />
  

  return (
    <Box>
      <h2>알림리스트</h2>
      <FormControlLabel
          control={
            <Checkbox
              checked={read}
              onChange={(event) => readAlarm(event.target.checked)}
            />
          }
          label="모두 읽음"
        />
      
      <Grid>
        
        <Grid>
          
          <div>
            <List read={read}>
              {generate(
                <ListItem
                  id='alarm-item'
                  secondaryAction={
                    <IconButton edge="end" onClick={(event) => readAlarm(event.target.checked)}>
                      <CloseIcon />
                    </IconButton>
                  }
                >
                  <ListItemAvatar>
                    {icon}
                  </ListItemAvatar>
                  <ListItemText primary="갈망포카" secondary="지금 당신의 갈망포카가 올라왔어요! 확인해보세요." />
                  
                </ListItem>,
                <ListItem
                secondaryAction={
                  <IconButton edge="end" aria-label="delete">
                    <CloseIcon />
                  </IconButton>
                }
              >
                <ListItemAvatar>
                    {icon}
                </ListItemAvatar>
                <ListItemText primary="채팅알림" secondary="{} 님이 채팅을 보냈습니다." />
              </ListItem>,
              )}
            </List>
            
          </div>
        </Grid>
      </Grid>
    </Box>
  );
}
